import React, {useState} from 'react';
import {Form, Input, Button, Checkbox, notification} from 'antd';
import { UserOutlined, LockOutlined    } from '@ant-design/icons';
import './RegisterForm.scss';
import {emailValidate, minLengthValidations} from '../../utils/formValidation';
export default function RegisterForm(){
    const [state, setstate] = useState({
        email:"",
        password:"",
        repeatPassword:"",
        privacyPolicie:false
    });

    const [formValid, setFormValid] = useState({
        email:false,
        password:false,
        repeatPassword:false,
        privacyPolicie:false
    });


    const changeForm = (e)=>{
        if(e.target.name === "privacyPolicie"){
            setstate({
                ...state,
                [e.target.name]:e.target.checked
            })
        }else{
            setstate({
                ...state,
                [e.target.name]:e.target.value
            })
        }
    }

    const changeValidation = (e) =>{
        const {type,name} = e.target;
        // eslint-disable-next-line default-case
        switch(type){
            case "email":
                setFormValid({
                    ...formValid
                    [name] = emailValidate(e.target)
                });
                break;
            case "password":
                setFormValid({
                    ...formValid
                    [name] = minLengthValidations(e.target,6)
                });                
                break;
            case "checkbox":
                setFormValid({
                    ...formValid
                    [name] = e.target.value
                });      
                break;
        }
        console.log('validando...');
        console.log(formValid);
    }

    const register = (e) =>{
        e.preventDefault();
        console.log(formValid);
    }

    return (
        <Form className="register-form" onChange={changeForm}>
            <Form.Item>
                <Input
                    prefix={<UserOutlined style={{color:"rgba(0,0,0,.25)"}}/>}
                    type="email"
                    name="email"
                    placeholder="Correo electronico"
                    className="register-form__input"
                    onChange={changeValidation}
                    value={state.email}
                />
            </Form.Item>
            <Form.Item>
                <Input
                    prefix={<LockOutlined  style={{color:"rgba(0,0,0,.25)"}}/>}
                    type="password"
                    name="password"
                    placeholder="contraseña"
                    className="register-form__input"
                    onChange={changeValidation}                    
                    value={state.password}
                />
            </Form.Item>  
            <Form.Item>
                <Input
                    prefix={<LockOutlined  style={{color:"rgba(0,0,0,.25)"}}/>}
                    type="password"
                    name="repeatPassword"
                    placeholder="repita la contraseña"
                    className="register-form__input"
                    onChange={changeValidation}                    
                    value={state.repeatPassword}
                />
            </Form.Item> 
            <Form.Item>
                <Checkbox name="privacyPolicie"
                    checked={state.privacyPolicie}
                    onChange={changeValidation}
                >
                    He leido las politicas de seguridad
                </Checkbox>
            </Form.Item>  
            <Form.Item>
                <Button className="register-form__button" onClick={register}>
                    Crear cuenta
                </Button>
            </Form.Item>                                                
        </Form>
    )
}
