import React, { useState,useEffect } from "react";
import "./ListMenus.scss";
import { EditOutlined,StopOutlined,DeleteOutlined, CheckOutlined,UserAddOutlined    } from "@ant-design/icons";
import { Tooltip ,  List, Button, Avatar, notification,  Modal as ModalAntd} from "antd";
import Modal from "../../../Admin/Modal";
//import User from "../../../../pages/Admin/Users/Users";
import {getMenus,addMenu} from '../../../../api/menu';
import { getAccessToken } from "../../../../api/auth";

const {confirm} = ModalAntd;

export default function ListUsers(props) {
  const {menus,setReloadMenus} = props;
  const token = getAccessToken();
  
  return (
    <div className="list-users">
      <div className="list-users__header">
        <div className="list-users__header-btn-add-user">
            <Button
              type="primary"
              onClick={() =>console.log("Crear Menu")}
              >        
              <UserAddOutlined />
              Nuevo menu
            </Button>
          </div>        
      </div>
      <MenuItems 
            menus={menus} 
            setReloadMenus = {setReloadMenus}
        />

      <Modal
        title={modalTitle}
        isVisible={modalCollapsed}
        setIsVisible={setmodalCollapsed}
      >
         {modalContent}
      </Modal>
    </div>
  );
}

function UsersActive(props) {
  const { usersActive, setmodalCollapsed, modalCollapsed,setModalContent,
     setModalTitle,setReloadUsers,deleteUser
    } = props;
    const token = getAccessToken();
    const editUser = user =>{
        //console.log("1."+user);
        setmodalCollapsed(!modalCollapsed)
        setModalTitle(`Editar ${user.name ? user.name : '...'} ${user.lastName ? user.lastName : '...'}`);
        setModalContent(<EditUserForm user={user}
           setmodalCollapsed={setmodalCollapsed}
           setReloadUsers={setReloadUsers}/>);
    }

    const desactiveUser = user =>{
      const body = {
        "active":false
      };
      activeDesactiveUserApi(token,body, user._id).then((result) => {
        notification["success"]({
          message: result.message,
        });
        setReloadUsers(true)
      });
    }
    
  return (
    <List
      className="users-active"
      itemLayout="horizontal"
      dataSource={usersActive}
      renderItem={user => 
      <UserActive 
        user={user} 
        editUser={editUser} 
        desactiveUser={desactiveUser}
        deleteUser={deleteUser}/>}
    />
  );
}

function UserActive(props){
  const {user, editUser,desactiveUser,deleteUser} = props;
  const [avatar, setAvatar] = useState(null);

  useEffect(()=>{
    if(user.avatar){
      getAvatarApi(user.avatar).then(response=>{
        setAvatar(response);
      })
    }else{
      setAvatar(null);
    }
  },[avatar]);


  return(
    <List.Item
    actions={[
      <Tooltip title="Editar">
      <Button
        type="primary"
        onClick={() => editUser(user)}
      >
        <EditOutlined />
      </Button>
      </Tooltip>,
      <Button
        type="danger"
        onClick={() => desactiveUser(user)}
      >
        <StopOutlined  />
        
      </Button>,
      <Button
        type="danger"
        onClick={() => deleteUser(user)}
      >
        <DeleteOutlined  />
      </Button>,
    ]}
  >
    <List.Item.Meta
      avatar={<Avatar src={avatar ? avatar : NoAvatar} />}
      title={`
                      ${user.name ? user.name : ".."}
                      ${user.lastName ? user.lastName : "..."}
                  `}
      description={user.email}
    />
  </List.Item>
  );
}
