import React, { useState, useEffect, useCallback } from "react";
import {
  Avatar,
  Form,
  Icon,
  Input,
  Select,
  Button,
  Row,
  Col,
  notification,
  message,
} from "antd";
import { useDropzone } from "react-dropzone";
import { UserOutlined, MailFilled } from "@ant-design/icons";
import NoAvatar from "../../../../assets/img/png/no-avatar.png";
import {
  editUserApi,
  getAvatarApi,
  updateUserApi,
  uploadAvatarApi,
} from "../../../../api/user";
import { getAccessToken } from "../../../../api/auth";
//import {getAvatarApi} from '../../../../api/user';
import "./CreateUserForm.scss";

export default function CreateUserForm(props) {
  const { setModalSignUpVisible } = props;

  return (
    <div className="edit-user-form">
      <CreateForm
      />
    </div>
  );
}

function CreateForm(props) {
  //const { userData, setUserData, updateUser } = props;
  const { Option } = Select;

  return (
    <Form className="form-edit">
      <Row gutter={24}>
        <Col span={12}>
          <Form.Item>
            <Input
              prefix={<UserOutlined />}
              placeholder="Nombre"
              value=""
            />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item>
            <Input
              prefix={<UserOutlined />}
              placeholder="Apellido"
              value=""
            />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={24}>
        <Col span={12}>
          <Form.Item>
            <Input
              prefix={<MailFilled />}
              placeholder="email"
              value=""
            />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item>
            <Select
              placeholder="Seleccione un rol"
              value=""
            >
              <Option value="admin">Administrador</Option>
              <Option value="editor">Editor</Option>
              <Option value="reviwer">Revisor</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={24}>
        <Col span={12}>
          <Form.Item>
            <Input
              prefix={<UserOutlined />}
              type="password"
              placeholder="contraseña"
            />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item>
            <Input
              prefix={<UserOutlined />}
              type="password"
              placeholder="Repetir contraseña"
            />
          </Form.Item>
        </Col>
      </Row>
      <Form.Item>
        <Button type="primary" className="btn-submit">
          Crear Usuario
        </Button>
      </Form.Item>
    </Form>
  );
}
