import React, { useState,useEffect } from "react";
import "./ListUsers.scss";
import { EditOutlined,StopOutlined,DeleteOutlined, CheckOutlined   } from "@ant-design/icons";
import { Switch, List, Button, Avatar, notification } from "antd";
import NoAvatar from "../../../../assets/img/png/no-avatar.png";
import Modal from "../../../Admin/Modal";
//import User from "../../../../pages/Admin/Users/Users";
import {getAvatarApi,activeDesactiveUserApi} from '../../../../api/user';
import EditUserForm from "../../../../components/Admin/User/EditUserForm";
import { getAccessToken } from "../../../../api/auth";

export default function ListUsers(props) {
  const [modalCollapsed, setmodalCollapsed] = useState(false);
  const { usersActive, usersInactive,setReloadUsers } = props;
  const [viewUsersActives, setViewUsersActives] = useState(true);
  const [modalTitle, setModalTitle] =  useState("");
  const [modalContent, setModalContent] =  useState(null);

  return (
    <div className="list-users">
      <div className="list-users__switch">
        <Switch
          defaultChecked
          onChange={() => setViewUsersActives(!viewUsersActives)}
        />
        <span>
          {viewUsersActives ? "UsuariosActivos" : "Usuarios inactivos"}
        </span>
      </div>
      {viewUsersActives ? (
        <UsersActive 
            usersActive={usersActive} 
            setmodalCollapsed = {setmodalCollapsed}
            modalCollapsed = {modalCollapsed}
            setModalTitle = {setModalTitle}
            setModalContent = {setModalContent}
            setReloadUsers = {setReloadUsers}

        />
      ) : (
        <UsersInactive usersInactive = {usersInactive} />
      )}

      <Modal
        title={modalTitle}
        isVisible={modalCollapsed}
        setIsVisible={setmodalCollapsed}
      >
         {modalContent}
      </Modal>
    </div>
  );
}

function UsersActive(props) {
  const { usersActive, setmodalCollapsed, modalCollapsed,setModalContent,
     setModalTitle,setReloadUsers
    } = props;
    const token = getAccessToken();
    const editUser = user =>{
        //console.log("1."+user);
        setmodalCollapsed(!modalCollapsed)
        setModalTitle(`Editar ${user.name ? user.name : '...'} ${user.lastName ? user.lastName : '...'}`);
        setModalContent(<EditUserForm user={user}
           setmodalCollapsed={setmodalCollapsed}
           setReloadUsers={setReloadUsers}/>);
    }

    const desactiveUser = user =>{
      const body = {
        "active":false
      };
      activeDesactiveUserApi(token,body, user._id).then((result) => {
        notification["success"]({
          message: result.message,
        });
        setReloadUsers(true)
      });
    }
    
  return (
    <List
      className="users-active"
      itemLayout="horizontal"
      dataSource={usersActive}
      renderItem={user => <UserActive user={user} editUser={editUser} desactiveUser={desactiveUser}/>}
    />
  );
}

function UserActive(props){
  const {user, editUser,desactiveUser} = props;
  const [avatar, setAvatar] = useState(null);

  useEffect(()=>{
    if(user.avatar){
      getAvatarApi(user.avatar).then(response=>{
        setAvatar(response);
      })
    }else{
      setAvatar(null);
    }
  },[avatar]);


  return(
    <List.Item
    actions={[
      <Button
        type="primary"
        onClick={() => editUser(user)}
      >
        <EditOutlined />
      </Button>,
      <Button
        type="danger"
        onClick={() => desactiveUser(user)}
      >
        <StopOutlined  />
      </Button>,
      <Button
        type="danger"
        onClick={() => console.log("Eliminar usuario")}
      >
        <DeleteOutlined  />
      </Button>,
    ]}
  >
    <List.Item.Meta
      avatar={<Avatar src={avatar ? avatar : NoAvatar} />}
      title={`
                      ${user.name ? user.name : ".."}
                      ${user.lastName ? user.lastName : "..."}
                  `}
      description={user.email}
    />
  </List.Item>
  );
}

function UsersInactive(props) {
    const { usersInactive,setReloadUsers } = props;
    const token = getAccessToken();
    const activarUsuario = user =>{
      const body = {
        "active":true
      };
      activeDesactiveUserApi(token,body, user._id).then((result) => {
        notification["success"]({
          message: result.message,
        });
        setReloadUsers(true)
      });
    }

    return (
        <List
          className="users-active"
          itemLayout="horizontal"
          dataSource={usersInactive}
          renderItem={user => <UserInactive user={user} activarUsuario={activarUsuario}/>}
        />
      );
}

function UserInactive(props){
  const {user} = props;
  const [avatar, setAvatar] = useState(null);

  useEffect(()=>{
    if(user.avatar){
      getAvatarApi(user.avatar).then(response=>{
        setAvatar(response);
      })
    }else{
      setAvatar(null);
    }
  },[avatar]);  

  return (
    <List.Item
    actions={[
      <Button
        type="primary"
        onClick={() => console.log("Activar usuario")}
      >
        <CheckOutlined />
      </Button>,
      <Button
        type="danger"
        onClick={() => console.log("Eliminar usuario")}
      >
        <DeleteOutlined  />
      </Button>,
    ]}
  >
    <List.Item.Meta
      avatar={<Avatar src={avatar ? avatar : NoAvatar} />}
      title={`
                      ${user.name ? user.name : ".."}
                      ${user.lastName ? user.lastName : "..."}
                  `}
      description={user.email}
    />
  </List.Item>
  );
}
